parameters.jsCode:
let article = "";

// Introduction
article += $('Project Planner').first().json.message.content.introduction;

for (const item of $input.all()) {
  article += "<strong>" + item.json.title + "</strong>";
  article += item.json.output;
  article += "<br>";
}

// Conclusions
article += "<strong> Conclusions </strong>";
article += $('Project Planner').first().json.message.content.conclusions;


return [
  {
    "article": article
  }
];

